public class SmallestNumber {
    public static int findSmallestNumber(int[] nums) {
        int smallest = nums[0];
        for (int i = 1; i < nums.length; i++) {
            if (nums[i] < smallest) {
                smallest = nums[i];
            }
        }
        return smallest;
    }

    public static void main(String[] args) {
        int[] nums = {1, 2, 3, 4, 5};
        int result = findSmallestNumber(nums);
        System.out.println(result); // Output: 1
    }
}

public class ReverseString {
    public static String reverseString(String str) {
        StringBuilder sb = new StringBuilder(str);
        return sb.reverse().toString();
    }

    public static void main(String[] args) {
        String str = "World";
        String result = reverseString(str);
        System.out.println(result); // Output: dlroW
    }
}

public class CalculateSum {
    public static int calculateSum(int n1, int n2) {
        return n1 + n2;
    }

    public static void main(String[] args) {
        int n1 = 34;
        int n2 = 45;
        int result = calculateSum(n1, n2);
        System.out.println(result); // Output: 79
    }
}
